    /**
     * Transforms a ResultSet iterating on the appeal on a AppealBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting AppealBean pObject
     */
    //72
    public AppealBean decodeRow(ResultSet rs) throws SQLException
    {
        AppealBean pObject = createAppealBean();
        pObject.setAppealid(Manager.getLong(rs, 1));
        pObject.setContractid(Manager.getLong(rs, 2));
        pObject.setNum(rs.getString(3));
        pObject.setAppealdate(rs.getDate(4));
        pObject.setPath(rs.getString(5));
        pObject.setSamplenum(Manager.getDouble(rs, 6));
        pObject.setRegbyid(Manager.getInteger(rs, 7));
        pObject.setRegdate(rs.getTimestamp(8));
        pObject.setRegionid(Manager.getInteger(rs, 9));
        pObject.setRaionid(Manager.getInteger(rs, 10));
        pObject.setVillageid(Manager.getInteger(rs, 11));
        pObject.setDeleted(Manager.getBoolean(rs, 12));
        pObject.setBringer(rs.getString(13));
        pObject.setStamp(rs.getString(14));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the appeal table on a AppealBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting AppealBean pObject
     */
    //73
    public AppealBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        AppealBean pObject = createAppealBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_APPEALID:
                    ++pos;
                    pObject.setAppealid(Manager.getLong(rs, pos));
                    break;
                case ID_CONTRACTID:
                    ++pos;
                    pObject.setContractid(Manager.getLong(rs, pos));
                    break;
                case ID_NUM:
                    ++pos;
                    pObject.setNum(rs.getString(pos));
                    break;
                case ID_APPEALDATE:
                    ++pos;
                    pObject.setAppealdate(rs.getDate(pos));
                    break;
                case ID_PATH:
                    ++pos;
                    pObject.setPath(rs.getString(pos));
                    break;
                case ID_SAMPLENUM:
                    ++pos;
                    pObject.setSamplenum(Manager.getDouble(rs, pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_REGIONID:
                    ++pos;
                    pObject.setRegionid(Manager.getInteger(rs, pos));
                    break;
                case ID_RAIONID:
                    ++pos;
                    pObject.setRaionid(Manager.getInteger(rs, pos));
                    break;
                case ID_VILLAGEID:
                    ++pos;
                    pObject.setVillageid(Manager.getInteger(rs, pos));
                    break;
                case ID_DELETED:
                    ++pos;
                    pObject.setDeleted(Manager.getBoolean(rs, pos));
                    break;
                case ID_BRINGER:
                    ++pos;
                    pObject.setBringer(rs.getString(pos));
                    break;
                case ID_STAMP:
                    ++pos;
                    pObject.setStamp(rs.getString(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

