    /**
     * Transforms a ResultSet iterating on the bill on a BillBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting BillBean pObject
     */
    //72
    public BillBean decodeRow(ResultSet rs) throws SQLException
    {
        BillBean pObject = createBillBean();
        pObject.setBillid(Manager.getLong(rs, 1));
        pObject.setNum(rs.getString(2));
        pObject.setContractid(Manager.getLong(rs, 3));
        pObject.setPath(rs.getString(4));
        pObject.setRegdate(rs.getTimestamp(5));
        pObject.setRegbyid(Manager.getInteger(rs, 6));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the bill table on a BillBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting BillBean pObject
     */
    //73
    public BillBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        BillBean pObject = createBillBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_BILLID:
                    ++pos;
                    pObject.setBillid(Manager.getLong(rs, pos));
                    break;
                case ID_NUM:
                    ++pos;
                    pObject.setNum(rs.getString(pos));
                    break;
                case ID_CONTRACTID:
                    ++pos;
                    pObject.setContractid(Manager.getLong(rs, pos));
                    break;
                case ID_PATH:
                    ++pos;
                    pObject.setPath(rs.getString(pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

