    /**
     * Transforms a ResultSet iterating on the device on a DeviceBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting DeviceBean pObject
     */
    //72
    public DeviceBean decodeRow(ResultSet rs) throws SQLException
    {
        DeviceBean pObject = createDeviceBean();
        pObject.setDeviceid(Manager.getInteger(rs, 1));
        pObject.setName(rs.getString(2));
        pObject.setModel(rs.getString(3));
        pObject.setSn(rs.getString(4));
        pObject.setCreatedate(rs.getDate(5));
        pObject.setConditioin(rs.getString(6));
        pObject.setInstitutionid(Manager.getInteger(rs, 7));
        pObject.setDeleted(Manager.getBoolean(rs, 8));
        pObject.setHasplate(Manager.getBoolean(rs, 9));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the device table on a DeviceBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting DeviceBean pObject
     */
    //73
    public DeviceBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        DeviceBean pObject = createDeviceBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_DEVICEID:
                    ++pos;
                    pObject.setDeviceid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_MODEL:
                    ++pos;
                    pObject.setModel(rs.getString(pos));
                    break;
                case ID_SN:
                    ++pos;
                    pObject.setSn(rs.getString(pos));
                    break;
                case ID_CREATEDATE:
                    ++pos;
                    pObject.setCreatedate(rs.getDate(pos));
                    break;
                case ID_CONDITIOIN:
                    ++pos;
                    pObject.setConditioin(rs.getString(pos));
                    break;
                case ID_INSTITUTIONID:
                    ++pos;
                    pObject.setInstitutionid(Manager.getInteger(rs, pos));
                    break;
                case ID_DELETED:
                    ++pos;
                    pObject.setDeleted(Manager.getBoolean(rs, pos));
                    break;
                case ID_HASPLATE:
                    ++pos;
                    pObject.setHasplate(Manager.getBoolean(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

