    /**
     * Transforms a ResultSet iterating on the deviceresult on a DeviceresultBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting DeviceresultBean pObject
     */
    //72
    public DeviceresultBean decodeRow(ResultSet rs) throws SQLException
    {
        DeviceresultBean pObject = createDeviceresultBean();
        pObject.setDeviceresultid(Manager.getLong(rs, 1));
        pObject.setDeviceid(Manager.getInteger(rs, 2));
        pObject.setResultdate(rs.getTimestamp(3));
        pObject.setResult(rs.getString(4));
        pObject.setRegbyid(Manager.getInteger(rs, 5));
        pObject.setRegdate(rs.getTimestamp(6));
        pObject.setSampleitemreseachid(Manager.getLong(rs, 7));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the deviceresult table on a DeviceresultBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting DeviceresultBean pObject
     */
    //73
    public DeviceresultBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        DeviceresultBean pObject = createDeviceresultBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_DEVICERESULTID:
                    ++pos;
                    pObject.setDeviceresultid(Manager.getLong(rs, pos));
                    break;
                case ID_DEVICEID:
                    ++pos;
                    pObject.setDeviceid(Manager.getInteger(rs, pos));
                    break;
                case ID_RESULTDATE:
                    ++pos;
                    pObject.setResultdate(rs.getTimestamp(pos));
                    break;
                case ID_RESULT:
                    ++pos;
                    pObject.setResult(rs.getString(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_SAMPLEITEMRESEACHID:
                    ++pos;
                    pObject.setSampleitemreseachid(Manager.getLong(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

