    /**
     * Transforms a ResultSet iterating on the docs on a DocsBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting DocsBean pObject
     */
    //72
    public DocsBean decodeRow(ResultSet rs) throws SQLException
    {
        DocsBean pObject = createDocsBean();
        pObject.setDocsid(Manager.getLong(rs, 1));
        pObject.setDoctypeid(Manager.getInteger(rs, 2));
        pObject.setContractid(Manager.getLong(rs, 3));
        pObject.setPath(rs.getString(4));
        pObject.setRegdate(rs.getTimestamp(5));
        pObject.setRegbyid(Manager.getInteger(rs, 6));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the docs table on a DocsBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting DocsBean pObject
     */
    //73
    public DocsBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        DocsBean pObject = createDocsBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_DOCSID:
                    ++pos;
                    pObject.setDocsid(Manager.getLong(rs, pos));
                    break;
                case ID_DOCTYPEID:
                    ++pos;
                    pObject.setDoctypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_CONTRACTID:
                    ++pos;
                    pObject.setContractid(Manager.getLong(rs, pos));
                    break;
                case ID_PATH:
                    ++pos;
                    pObject.setPath(rs.getString(pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

