    /**
     * Looks for the number of elements of a specific ExtravalBean pObject given a c
     *
     * @param pObject the ExtravalBean pObject to look for
     * @return the number of rows returned
     */
    //83
    public int countUsingTemplate(ExtravalBean pObject) throws SQLException
    {
        StringBuffer where = new StringBuffer("");
        Connection c = null;
        PreparedStatement ps = null;
        StringBuffer _sql = null;
        StringBuffer _sqlWhere = null;
    
        try
        {
                _sql = new StringBuffer("SELECT count(*) as MCOUNT  from extraval WHERE ");
                _sqlWhere = new StringBuffer("");
                int _dirtyCount = 0;
    
                if (pObject.isExtraparamidModified()) {
                    _dirtyCount++; 
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("extraparamid= ?");
                }
    
                if (pObject.isContractsampleidModified()) {
                    _dirtyCount++; 
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("contractsampleid= ?");
                }
    
                if (pObject.isBvalModified()) {
                    _dirtyCount++; 
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("bval= ?");
                }
    
                if (pObject.isDvalModified()) {
                    _dirtyCount++; 
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("dval= ?");
                }
    
                if (pObject.isTvalModified()) {
                    _dirtyCount++; 
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("tval= ?");
                }
    
                if (_dirtyCount == 0)
                   throw new SQLException ("The pObject to look is unvalid : not initialized !");
    
                _sql.append(_sqlWhere);
                c = getConnection();
                ps = c.prepareStatement(_sql.toString(),ResultSet.TYPE_SCROLL_INSENSITIVE, ResultSet.CONCUR_READ_ONLY);
    
                _dirtyCount = 0;
    
                if (pObject.isExtraparamidModified()) {
                    Manager.setLong(ps, ++_dirtyCount, pObject.getExtraparamid());
                }
    
                if (pObject.isContractsampleidModified()) {
                    Manager.setLong(ps, ++_dirtyCount, pObject.getContractsampleid());
                }
    
                if (pObject.isBvalModified()) {
                    Manager.setBoolean(ps, ++_dirtyCount, pObject.getBval());
                }
    
                if (pObject.isDvalModified()) {
                    Manager.setDouble(ps, ++_dirtyCount, pObject.getDval());
                }
    
                if (pObject.isTvalModified()) {
                    ps.setString(++_dirtyCount, pObject.getTval());
                }
    
                return countByPreparedStatement(ps);
        }
        finally
        {
            getManager().close(ps);
            freeConnection(c);
        }
    }



