    /**
     * Transforms a ResultSet iterating on the extraval on a ExtravalBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting ExtravalBean pObject
     */
    //72
    public ExtravalBean decodeRow(ResultSet rs) throws SQLException
    {
        ExtravalBean pObject = createExtravalBean();
        pObject.setExtraparamid(Manager.getLong(rs, 1));
        pObject.setContractsampleid(Manager.getLong(rs, 2));
        pObject.setBval(Manager.getBoolean(rs, 3));
        pObject.setDval(Manager.getDouble(rs, 4));
        pObject.setTval(rs.getString(5));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the extraval table on a ExtravalBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ExtravalBean pObject
     */
    //73
    public ExtravalBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ExtravalBean pObject = createExtravalBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_EXTRAPARAMID:
                    ++pos;
                    pObject.setExtraparamid(Manager.getLong(rs, pos));
                    break;
                case ID_CONTRACTSAMPLEID:
                    ++pos;
                    pObject.setContractsampleid(Manager.getLong(rs, pos));
                    break;
                case ID_BVAL:
                    ++pos;
                    pObject.setBval(Manager.getBoolean(rs, pos));
                    break;
                case ID_DVAL:
                    ++pos;
                    pObject.setDval(Manager.getDouble(rs, pos));
                    break;
                case ID_TVAL:
                    ++pos;
                    pObject.setTval(rs.getString(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

