    /**
     * Transforms a ResultSet iterating on the inventory on a InventoryBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting InventoryBean pObject
     */
    //72
    public InventoryBean decodeRow(ResultSet rs) throws SQLException
    {
        InventoryBean pObject = createInventoryBean();
        pObject.setInventoryid(Manager.getInteger(rs, 1));
        pObject.setInstitutionid(Manager.getInteger(rs, 2));
        pObject.setStartdate(rs.getTimestamp(3));
        pObject.setEnddate(rs.getTimestamp(4));
        pObject.setRegbyid(Manager.getInteger(rs, 5));
        pObject.setRegdate(rs.getTimestamp(6));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the inventory table on a InventoryBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting InventoryBean pObject
     */
    //73
    public InventoryBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        InventoryBean pObject = createInventoryBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_INVENTORYID:
                    ++pos;
                    pObject.setInventoryid(Manager.getInteger(rs, pos));
                    break;
                case ID_INSTITUTIONID:
                    ++pos;
                    pObject.setInstitutionid(Manager.getInteger(rs, pos));
                    break;
                case ID_STARTDATE:
                    ++pos;
                    pObject.setStartdate(rs.getTimestamp(pos));
                    break;
                case ID_ENDDATE:
                    ++pos;
                    pObject.setEnddate(rs.getTimestamp(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

