    /**
     * Transforms a ResultSet iterating on the invoice on a InvoiceBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting InvoiceBean pObject
     */
    //72
    public InvoiceBean decodeRow(ResultSet rs) throws SQLException
    {
        InvoiceBean pObject = createInvoiceBean();
        pObject.setInvoiceid(Manager.getLong(rs, 1));
        pObject.setNum(rs.getString(2));
        pObject.setInvoicedate(rs.getDate(3));
        pObject.setPrice(Manager.getDouble(rs, 4));
        pObject.setVat(Manager.getDouble(rs, 5));
        pObject.setOperid(Manager.getInteger(rs, 6));
        pObject.setContragentid(Manager.getLong(rs, 7));
        pObject.setRegbyid(Manager.getInteger(rs, 8));
        pObject.setRegdate(rs.getTimestamp(9));
        pObject.setInvoicetypeid(Manager.getInteger(rs, 10));
        pObject.setInvoicesourcetypeid(Manager.getInteger(rs, 11));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the invoice table on a InvoiceBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting InvoiceBean pObject
     */
    //73
    public InvoiceBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        InvoiceBean pObject = createInvoiceBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_INVOICEID:
                    ++pos;
                    pObject.setInvoiceid(Manager.getLong(rs, pos));
                    break;
                case ID_NUM:
                    ++pos;
                    pObject.setNum(rs.getString(pos));
                    break;
                case ID_INVOICEDATE:
                    ++pos;
                    pObject.setInvoicedate(rs.getDate(pos));
                    break;
                case ID_PRICE:
                    ++pos;
                    pObject.setPrice(Manager.getDouble(rs, pos));
                    break;
                case ID_VAT:
                    ++pos;
                    pObject.setVat(Manager.getDouble(rs, pos));
                    break;
                case ID_OPERID:
                    ++pos;
                    pObject.setOperid(Manager.getInteger(rs, pos));
                    break;
                case ID_CONTRAGENTID:
                    ++pos;
                    pObject.setContragentid(Manager.getLong(rs, pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_INVOICETYPEID:
                    ++pos;
                    pObject.setInvoicetypeid(Manager.getInteger(rs, pos));
                    break;
                case ID_INVOICESOURCETYPEID:
                    ++pos;
                    pObject.setInvoicesourcetypeid(Manager.getInteger(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

