    /**
     * Transforms a ResultSet iterating on the messages on a MessagesBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting MessagesBean pObject
     */
    //72
    public MessagesBean decodeRow(ResultSet rs) throws SQLException
    {
        MessagesBean pObject = createMessagesBean();
        pObject.setMessagesid(Manager.getLong(rs, 1));
        pObject.setMessagedate(rs.getTimestamp(2));
        pObject.setPersonellid(Manager.getInteger(rs, 3));
        pObject.setContent(rs.getString(4));
        pObject.setRead(Manager.getBoolean(rs, 5));
        pObject.setReaddate(rs.getTimestamp(6));
        pObject.setDeleted(Manager.getBoolean(rs, 7));
        pObject.setDeletedate(rs.getTimestamp(8));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the messages table on a MessagesBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting MessagesBean pObject
     */
    //73
    public MessagesBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        MessagesBean pObject = createMessagesBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_MESSAGESID:
                    ++pos;
                    pObject.setMessagesid(Manager.getLong(rs, pos));
                    break;
                case ID_MESSAGEDATE:
                    ++pos;
                    pObject.setMessagedate(rs.getTimestamp(pos));
                    break;
                case ID_PERSONELLID:
                    ++pos;
                    pObject.setPersonellid(Manager.getInteger(rs, pos));
                    break;
                case ID_CONTENT:
                    ++pos;
                    pObject.setContent(rs.getString(pos));
                    break;
                case ID_READ:
                    ++pos;
                    pObject.setRead(Manager.getBoolean(rs, pos));
                    break;
                case ID_READDATE:
                    ++pos;
                    pObject.setReaddate(rs.getTimestamp(pos));
                    break;
                case ID_DELETED:
                    ++pos;
                    pObject.setDeleted(Manager.getBoolean(rs, pos));
                    break;
                case ID_DELETEDATE:
                    ++pos;
                    pObject.setDeletedate(rs.getTimestamp(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

