    /**
     * Transforms a ResultSet iterating on the plate on a PlateBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting PlateBean pObject
     */
    //72
    public PlateBean decodeRow(ResultSet rs) throws SQLException
    {
        PlateBean pObject = createPlateBean();
        pObject.setPlateid(Manager.getLong(rs, 1));
        pObject.setNum(rs.getString(2));
        pObject.setPlatedate(rs.getTimestamp(3));
        pObject.setRegbyid(Manager.getInteger(rs, 4));
        pObject.setRegdate(rs.getTimestamp(5));
        pObject.setDeviceid(Manager.getInteger(rs, 6));
        pObject.setPlatetemplateid(Manager.getLong(rs, 7));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the plate table on a PlateBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting PlateBean pObject
     */
    //73
    public PlateBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        PlateBean pObject = createPlateBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_PLATEID:
                    ++pos;
                    pObject.setPlateid(Manager.getLong(rs, pos));
                    break;
                case ID_NUM:
                    ++pos;
                    pObject.setNum(rs.getString(pos));
                    break;
                case ID_PLATEDATE:
                    ++pos;
                    pObject.setPlatedate(rs.getTimestamp(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
                case ID_DEVICEID:
                    ++pos;
                    pObject.setDeviceid(Manager.getInteger(rs, pos));
                    break;
                case ID_PLATETEMPLATEID:
                    ++pos;
                    pObject.setPlatetemplateid(Manager.getLong(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

