    /**
     * Transforms a ResultSet iterating on the postponeitem on a PostponeitemBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting PostponeitemBean pObject
     */
    //72
    public PostponeitemBean decodeRow(ResultSet rs) throws SQLException
    {
        PostponeitemBean pObject = createPostponeitemBean();
        pObject.setPostponeitemid(Manager.getLong(rs, 1));
        pObject.setContractsampleitemid(Manager.getLong(rs, 2));
        pObject.setEnddate(rs.getDate(3));
        pObject.setRegbyid(Manager.getInteger(rs, 4));
        pObject.setRegdate(rs.getTimestamp(5));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the postponeitem table on a PostponeitemBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting PostponeitemBean pObject
     */
    //73
    public PostponeitemBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        PostponeitemBean pObject = createPostponeitemBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_POSTPONEITEMID:
                    ++pos;
                    pObject.setPostponeitemid(Manager.getLong(rs, pos));
                    break;
                case ID_CONTRACTSAMPLEITEMID:
                    ++pos;
                    pObject.setContractsampleitemid(Manager.getLong(rs, pos));
                    break;
                case ID_ENDDATE:
                    ++pos;
                    pObject.setEnddate(rs.getDate(pos));
                    break;
                case ID_REGBYID:
                    ++pos;
                    pObject.setRegbyid(Manager.getInteger(rs, pos));
                    break;
                case ID_REGDATE:
                    ++pos;
                    pObject.setRegdate(rs.getTimestamp(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

