    /**
     * Transforms a ResultSet iterating on the productgroup on a ProductgroupBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting ProductgroupBean pObject
     */
    //72
    public ProductgroupBean decodeRow(ResultSet rs) throws SQLException
    {
        ProductgroupBean pObject = createProductgroupBean();
        pObject.setProductgroupid(Manager.getInteger(rs, 1));
        pObject.setParentid(Manager.getInteger(rs, 2));
        pObject.setName(rs.getString(3));
        pObject.setCode(rs.getString(4));
        pObject.setCompanyid(Manager.getInteger(rs, 5));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the productgroup table on a ProductgroupBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ProductgroupBean pObject
     */
    //73
    public ProductgroupBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ProductgroupBean pObject = createProductgroupBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_PRODUCTGROUPID:
                    ++pos;
                    pObject.setProductgroupid(Manager.getInteger(rs, pos));
                    break;
                case ID_PARENTID:
                    ++pos;
                    pObject.setParentid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_CODE:
                    ++pos;
                    pObject.setCode(rs.getString(pos));
                    break;
                case ID_COMPANYID:
                    ++pos;
                    pObject.setCompanyid(Manager.getInteger(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

