    public ProductunitBean decodeRow(ResultSet rs) throws SQLException
    {
        ProductunitBean pObject = createProductunitBean();
        pObject.setProductid(Manager.getLong(rs, 1));
        pObject.setPunitid(Manager.getInteger(rs, 2));
        pObject.setAmount(Manager.getDouble(rs, 3));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the productunit table on a ProductunitBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting ProductunitBean pObject
     */
    //73
    public ProductunitBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        ProductunitBean pObject = createProductunitBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_PRODUCTID:
                    ++pos;
                    pObject.setProductid(Manager.getLong(rs, pos));
                    break;
                case ID_PUNITID:
                    ++pos;
                    pObject.setPunitid(Manager.getInteger(rs, pos));
                    break;
                case ID_AMOUNT:
                    ++pos;
                    pObject.setAmount(Manager.getDouble(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

