    ///////////////////////////////////////////////////////////////////////
    /**
     * Transforms a ResultSet iterating on the tunit on a TunitBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting TunitBean pObject
     */
    //72
    public TunitBean decodeRow(ResultSet rs) throws SQLException
    {
        TunitBean pObject = createTunitBean();
        pObject.setTunitid(Manager.getInteger(rs, 1));
        pObject.setCountryid(Manager.getInteger(rs, 2));
        pObject.setName(rs.getString(3));
        pObject.setCode(rs.getString(4));
        pObject.setPunitid(Manager.getInteger(rs, 5));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the tunit table on a TunitBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting TunitBean pObject
     */
    //73
    public TunitBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        TunitBean pObject = createTunitBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_TUNITID:
                    ++pos;
                    pObject.setTunitid(Manager.getInteger(rs, pos));
                    break;
                case ID_COUNTRYID:
                    ++pos;
                    pObject.setCountryid(Manager.getInteger(rs, pos));
                    break;
                case ID_NAME:
                    ++pos;
                    pObject.setName(rs.getString(pos));
                    break;
                case ID_CODE:
                    ++pos;
                    pObject.setCode(rs.getString(pos));
                    break;
                case ID_PUNITID:
                    ++pos;
                    pObject.setPunitid(Manager.getInteger(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

